from pathlib import Path
text = Path('knowzcode/knowzcode_tracker.md').read_text()
rows = []
for line in text.splitlines():
    if line.startswith('|') and '`' in line:
        cols = [c.strip() for c in line.strip('|').split('|')]
        if len(cols) >= 9 and cols[2] != 'Node ID':
            rows.append({'status': cols[0], 'workgroup_id': cols[1], 'node_id': cols[2].strip('`'), 'notes': cols[8]})
outputs['rows'] = rows
